mapper.select:
select
id, category_id, spec_key, status, create_time, update_time
from h_spec


        and id = #{id}


        and category_id = #{categoryId}


        and spec_key = #{specKey}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from h_spec


        and id = #{id}


        and category_id = #{categoryId}


        and spec_key = #{specKey}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
